import { useState } from "react";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import "./ExpensesList.css";

const ExpensesList = (props) => {
  const [savedYear, setSavedYear] = useState("2020");
  const savePickedYear = (pickedYear) => {
    setSavedYear(pickedYear);
  };

  const filteredList = props.items.filter(
    (expense) => expense.date.getFullYear() === savedYear
  );

  console.log(props.items.map((expense) => expense.date.getFullYear()));
  console.log(filteredList);

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter onPickedYear={savePickedYear} selected={savedYear} />
        {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default ExpensesList;
